import java.util.*;

public class EfficacitePire {public static void main(String[] args) {String str = "Il fait beau aujourd'hui comme en aout";ArrayList<Character> ordre = new ArrayList<>(Arrays.asList('f', 'I', 'z', 'u', 'k', 'a', 'b', 'o'));ArrayList<String> coucou = texttolist(str);coucou = triermot(coucou, ordre);nafmapuce(coucou);}public static List<String> solution(String text, List<Character> pulet) {ArrayList<Character> ordre = new ArrayList<>(pulet);ArrayList<String> coucou = texttolist(text);coucou = triermot(coucou, ordre);if (coucou.size() == 0) {return List.of("");}return coucou;}public static void nafmapuce(ArrayList<String> youhou) {System.out.print("[");for (int j = 0; j < youhou.size() - 1; j++) {System.out.print(youhou.get(j) + " ,");}System.out.print(youhou.get(youhou.size() - 1) + "]");}public static ArrayList<String> texttolist(String str) {str = str.replaceAll("[^a-zA-Z0-9]+", " ").trim();String mot = "";ArrayList<String> motsep = new ArrayList<String>();for (int index = 0; index < str.length(); index++) {char car = str.charAt(index);if (car == ' ') {motsep.add(mot.trim());mot = "";}mot += car;}if (mot != "") {motsep.add(mot.trim());}return motsep;}public static ArrayList<String> triermot(ArrayList<String> motsep, ArrayList<Character> ordre) {ArrayList<String> motus = new ArrayList<String>();int u = 0;for (int i = 0; i < ordre.size(); i++) {for (int j = 0; j < motsep.size(); j++) {char lettre1 = (motsep.get(j).charAt(0));if (lettre1 == ((char) ordre.get(i))) {motus.add(motsep.get(j));u++;}}}for (int j = 0; j < motsep.size(); j++) {if (!ordre.contains(motsep.get(j).charAt(0))) {motus.add(motsep.get(j));u++;}}return motus;}}
